import React from 'react';
import {Form, Input, Button} from 'antd'

class GameOver extends React.Component {

    constructor (props) {
        super(props)
        this.state = {
            score: props.location.state.score,
            name: ""
        }
    }

    playAgain = () => {
        this.props.history.push("/game")
    }

    saveScore = () => {
        fetch('/api/record', {
            method: 'post',
            headers: {'Content-Type':'application/json'},
            body: JSON.stringify({
                "name": this.state.name,
                "score": this.state.score
            })
        })
        .then(res =>  res.json())
        .then(data => {
            console.log(data)
        })
    }

    handleChange = (e) => {
        this.setState({name: e.target.value})
    }

    render(){
        return(
            <div className="gameOverScreen">
                <h1>GAME OVER</h1>
                <h4>Score: {this.state.score || 0}</h4>
                <Button type="default" onClick={this.playAgain}>Play again</Button>

                <div className="submitScore">
                    <Form className="login-form">
                        <p>Submit your score</p>
                        <Form.Item label="Name">
                            <Input type="text" value={this.state.name} onChange={this.handleChange}/>
                        </Form.Item>
                        <Button type="default" onClick={this.saveScore}>SUBMIT</Button>
                    </Form>
                </div>
            </div>
        )
    }
}

export default GameOver
