import React from 'react';

class Game extends React.Component{

    constructor(){
        super()
        this.state = {
            time: 0
        }
    }

    componentDidMount = () =>{
        this.mainLoop()
    }

    mainLoop = () => {
        let time = this.state.time
        time++
        this.setState({time})
        requestAnimationFrame(this.mainLoop);
    }

    render(){
        return(
            <div className="game">{this.state.time}</div>
        )
    }
}

export default Game
