import React from 'react';

class Game extends React.Component{
    constructor(){
        super()
        this.state = {
            time:0

        }

    }

    componentDidMount(){
        this.mainLoop()
    }

    mainLoop = function() {
        let time = this.state.time
        time++
        this.setState({time})
        requestAnimationFrame(mainLoop);
    }

    render(){
        return(
            <div className="game">{this.state.time}</div>
        )
    }
}

export default Game
