import React from 'react';

class Game extends React.Component{

    constructor(){
        super()
        this.state = {
            time: 0
        }
    }

    componentDidMount = () =>{
        this.mainLoop()
    }

    mainLoop = () => {
        let time = this.state.time
        time++
        this.setState({time})
        requestAnimationFrame(this.mainLoop);
    }

    stopGame = () => {
        this.setState({a: "123"})

    }

    render(){
        return(
            <div className="game">
                <h1>{this.state.time}</h1>
                <button onClick={this.stopGame()}>STOP</button>
                <p>{this.state.a}</p>
            </div>
        )
    }
}

export default Game
